import React, { useState } from "react";
import { useForm } from "react-hook-form";

function Registration() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({mode: "onChange"});


  const isDateWithinLast10Years = (value) => {
    const selectedDate = new Date(value);
    const currentDate = new Date();
    const tenYearsAgo = new Date();
    tenYearsAgo.setFullYear(currentDate.getFullYear() - 10);
  
    return selectedDate <= currentDate && selectedDate <= tenYearsAgo;
  };
  const validationRules = {
    name: {
      required: "**Title is required",
      minLength: {
        value: 3,
        message: "**Title must have at least 3 characters",
      },
    },
    email: {
      required: "Email is required",
      pattern: {
        value:
          /^[A-Za-z][A-Za-z0-9._%+-]*@[A-Za-z0-9-]+\.[A-Za-z]{2,}(?:\.[A-Za-z]{2,})?$/i,
        message: "Invalid email address",
      },
    },

    phone: {
      required: "**Phone number is required",
      pattern: {
        value: /^[6-9]{1}[0-9]{9}$/, // Allow only valid 10-digit phone numbers starting with digits 6-9
        message: "**Invalid phone number",
      },
    },



    date: {
      required: "**Date of Birth is required",
      pattern: {
        value: /^\d{4}-\d{2}-\d{2}$/, // Assumes the date is in the format YYYY-MM-DD
        message: "**Invalid date format. Please use YYYY-MM-DD",
      },
      validate: {
        withinLast10Years: (value) => isDateWithinLast10Years(value) || "**Date of Birth should not  be within the last 10 years",
      },
    },
};

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <div className="container mt-5">
      <h2>Registration Form</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">
            Name:
          </label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            {...register("name", validationRules.name)}
          />
          <p className="text-danger">{errors?.name && errors.name.message}</p>
        </div>

        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            Email:
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            {...register("email", validationRules.email)}
            />
            <p className="text-danger">{errors?.email && errors.email.message}</p>
          </div>

        <div className="mb-3">
          <label htmlFor="phone" className="form-label">
            Phone:
          </label>
          <input
            type="tel"
            className="form-control"
            id="phone"
            name="phone"
            {...register("phone", validationRules.phone)}
            />
            <p className="text-danger">{errors?.phone && errors.phone.message}</p>
          </div>
          <div className="mb-3">
          <label htmlFor="dob" className="form-label">
            Date of Birth:
          </label>
          <input
            type="date"
            className="form-control"
            id="dob"
            name="dob"
            {...register("dob", validationRules.date)}
          />
          <p className="text-danger">{errors?.dob && errors.dob.message}</p>
        </div>


        <button type="submit" className="btn btn-primary">
          Register
        </button>
      </form>
    </div>
  );
}

export default Registration;
